import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ name: "", job: "", username: "" });
  const getUsers = async () => {
    const response = await fetch("http://localhost:3001/getusers");
    const data = await response.json();
    setUsers(data);
  };

  const handleUsers = (e) => {
    e.preventDefault();
    getUsers();
  };

  const createNewUser = async (e) => {
    e.preventDefault();
    await fetch("localhost:3001/createuser", {
      method: "POST",
      body: JSON.stringify(newUser),
      headers: { "Content-type": "application/json; charset=UTF8" },
    });
  };
  return (
    <div className="App">
      <button type="submit" onClick={handleUsers}>
        Get users
      </button>
      <section>
        <form action="/localhost:3001/createuser">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            value={newUser.name}
            onChange={(e) => setNewUser({ className: e.target.value })}
          />
          <label htmlFor="job">Job: </label>
          <input
            type="text"
            value={newUser.job}
            onChange={(e) => setNewUser({ job: e.target.value })}
          />
          <label htmlFor="username" value={newUser.username}>
            Username:
          </label>
          <input
            type="text"
            name="username"
            onChange={(e) => setNewUser({ username: e.target.value })}
          />
          <button type="submit" onClick={createNewUser}>
            Add{" "}
          </button>
        </form>
      </section>
      {users.map((user) => {
        const { _id, name, job, username } = user;
        return (
          <article key={_id}>
            <h1>Name: {name} </h1>
            <h2>Job: {job}</h2>
            <p>Username: {username}</p>
          </article>
        );
      })}
    </div>
  );
}

export default App;
