import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);

  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [username, setUsername] = useState("");

  const getUsers = async () => {
    const response = await axios.get("http://localhost:3001/getusers");
    setUsers(response.data);
  };

  const createNewUser = async (e) => {
    e.preventDefault();
    const response = await axios.post("http://localhost:3001/createuser", {
      name,
      job,
      username,
    });

    // const { newName, newJob, newUsername } = response.data;
    alert(`user ${name} created \n  job: ${job} \n username:${username}`);
    setName("");
    setJob("");
    setUsername("");
    getUsers();
  };

  const deleteUser = async (_id) => {
    const response = await axios.delete(`http://localhost:3001/users/${_id}`);
    console.log(response.data);
    getUsers();
  };

  return (
    <div className="App">
      <button onClick={getUsers}>Get users</button>
      <section className="form">
        <form action="/localhost:3001/createuser">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <label htmlFor="job">Job: </label>
          <input
            type="text"
            value={job}
            onChange={(e) => setJob(e.target.value)}
          />
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button type="submit" onClick={createNewUser}>
            Add
          </button>
        </form>
      </section>
      {users.map((user) => {
        const { _id, name, job, username } = user;
        return (
          <article key={_id}>
            <h1>Name: {name} </h1>
            <h2>Job: {job}</h2>
            <p>Username: {username}</p>
            <button onClick={() => deleteUser(_id)}>Delete</button>
          </article>
        );
      })}
    </div>
  );
}

export default App;
