import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import axios from "axios";
import { connection } from "mongoose";

function App() {
  const [users, setUsers] = useState([]);

  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [username, setUsername] = useState("");
  const [isEditing, setIsEditing] = useState(false);

  const getUsers = async () => {
    const response = await axios.get("http://localhost:3001/getusers");
    setUsers(response.data);
  };

  const createNewUser = async (e) => {
    e.preventDefault();
    const response = await axios.post("http://localhost:3001/createuser", {
      name,
      job,
      username,
    });

    // const { newName, newJob, newUsername } = response.data;
    alert(`user ${name} created \n  job: ${job} \n username:${username}`);
    setName("");
    setJob("");
    setUsername("");
    getUsers();
  };

  const deleteUser = async (_id) => {
    const response = await axios.delete(`http://localhost:3001/users/${_id}`);
    console.log(response.data);
    getUsers();
  };

  const editUser = (_id) => {
    const editingUser = users.find((item) => item._id === _id);
    console.log(editingUser);
    setIsEditing(true);
  };

  return (
    <div className="App">
      <section className="form-container">
        <form className="form" action="/localhost:3001/createuser">
          <h3>Add User</h3>
          <label htmlFor="name">
            <p>Name: </p>
          </label>
          <input
            type="text"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <label htmlFor="job">
            <p>Job: </p>
          </label>
          <input
            type="text"
            value={job}
            onChange={(e) => setJob(e.target.value)}
          />
          <label htmlFor="username">
            <p>Username: </p>
          </label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button type="submit" onClick={createNewUser}>
            Add
          </button>
        </form>
      </section>
      <article>
        <button onClick={getUsers}>Get users</button>
      </article>
      {users.length > 0 && <h1>Users</h1>}
      <section className="user-container">
        {users.map((user) => {
          const { _id, name, job, username } = user;
          return (
            <article className={isEditing ? "editing " : "users"} key={_id}>
              {isEditing && (
                <>
                  <h1>
                    Name:
                    <input
                      type="text"
                      name="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                    />{" "}
                  </h1>

                  <h2>Job: {job}</h2>
                  <p>Username: {username}</p>
                  <button onClick={() => deleteUser(_id)}>Delete</button>
                  <button onClick={() => editUser(_id)}>Edit</button>
                </>
              )}
              <h1>Name: {name} </h1>
              <h2>Job: {job}</h2>
              <p>Username: {username}</p>
              <button onClick={() => deleteUser(_id)}>Delete</button>
              <button onClick={() => editUser(_id)}>Edit</button>
            </article>
          );
        })}
      </section>
    </div>
  );
}

export default App;
