import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  const [users, setUsers] = useState([]);

  const getUsers = async () => {
    const response = await fetch("http://localhost:3001/getusers");
    const data = await response.json();
    setUsers(data);
  };

  const handleUsers = (e) => {
    e.preventDefault();
    getUsers();
  };

  return (
    <div className="App">
      <button type="submit" onClick={handleUsers}>
        Get users
      </button>
      {users.map((user) => {
        const { _id, name, job, username } = user;
        return (
          <article key={_id}>
            <h1>Name: {name} </h1>
            <h2>Job: {job}</h2>
            <p>Username: {username}</p>
          </article>
        );
      })}
    </div>
  );
}

export default App;
